mapper.select:
SELECT TR.totalOfRecord, C.CameraId, C.CameraName, C.ImageURL, C.IPAddress, C.TypeDetect, ST.StoreId, C.UpdatedTime, C.StatusId, STT.StatusName FROM tblCamera C JOIN tblStatus STT ON C.StatusId = STT.StatusId
AND STT.StatusId = #{statusId}
LEFT JOIN tblStackProductCameraMapping SPCM ON SPCM.CameraId = C.CameraId AND SPCM.StatusId = 1 LEFT JOIN tblStack STK ON SPCM.StackId = STK.StackId AND STK.StatusId = 1 LEFT JOIN tblShelf SH ON STK.ShelfId = SH.ShelfId AND SH.StatusId != 2 LEFT JOIN tblStore ST ON SH.StoreId = ST.StoreId AND ST.StatusId != 2 LEFT JOIN tblShelfCameraMapping SCM ON C.CameraId = SCM.CameraId AND SCM.StatusId = 1 LEFT JOIN tblShelf SH2 ON SCM.ShelfId = SH2.ShelfId AND SH2.StatusId = 1 LEFT JOIN tblStore ST2 ON ST2.StoreId = SH2.StoreId AND ST2.StatusId != 2, (SELECT COUNT(C.CameraId) AS totalOfRecord FROM tblCamera C JOIN tblStatus STT ON C.StatusId = STT.StatusId
AND STT.StatusId = #{statusId}
LEFT JOIN tblStackProductCameraMapping SPCM ON SPCM.CameraId = C.CameraId AND SPCM.StatusId = 1 LEFT JOIN tblStack STK ON SPCM.StackId = STK.StackId AND STK.StatusId = 1 LEFT JOIN tblShelf SH ON STK.ShelfId = SH.ShelfId AND SH.StatusId != 2 LEFT JOIN tblStore ST ON SH.StoreId = ST.StoreId AND ST.StatusId != 2 LEFT JOIN tblShelfCameraMapping SCM ON C.CameraId = SCM.CameraId AND SCM.StatusId = 1 LEFT JOIN tblShelf SH2 ON SCM.ShelfId = SH2.ShelfId AND SH2.StatusId = 1 LEFT JOIN tblStore ST2 ON ST2.StoreId = SH2.StoreId AND ST2.StatusId != 2
ST.StoreId = #{storeId}
AND C.CameraName like '%' #{cameraName} '%'
) TR
ST.StoreId = #{storeId}
AND C.CameraName like '%' #{cameraName} '%'
ORDER BY C.UpdatedTime DESC LIMIT #{offSet}, #{fetchNext}

mapper.update:
UPDATE tblCamera
SET StatusId = #{statusId}

    , ReasonInactive = #{reasonInactive}

WHERE CameraId = #{cameraId}

mapper.select:
SELECT StatusId
FROM tblCamera
WHERE CameraId = #{cameraId}

SELECT C.CameraId, C.CameraName, C.ImageURL, C.IPAddress, C.TypeDetect, C.UpdatedTime, C.StatusId, STT.StatusName, STT.StatusName, STK.StackId, STK.Position, '' AS ShelfId, '' AS ShelfName, SPCM.AddedCameraTime FROM tblCamera C JOIN tblStatus STT ON C.StatusId = STT.StatusId LEFT JOIN tblStackProductCameraMapping SPCM ON SPCM.CameraId = C.CameraId AND SPCM.StatusId = 1 LEFT JOIN tblStack STK ON SPCM.StackId = STK.StackId AND STK.StatusId = 1 LEFT JOIN tblShelf SH ON STK.ShelfId = SH.ShelfId AND SH.StatusId != 2 JOIN tblStore ST ON SH.StoreId = ST.StoreId AND ST.StatusId != 2 AND ST.StoreId IS NOT NULL WHERE C.CameraId = #{cameraId}
AND ST.StoreId = #{storeId}
UNION ALL
SELECT C.CameraId, C.CameraName, C.ImageURL, C.IPAddress, C.TypeDetect, STT.StatusName, C.UpdatedTime, C.StatusId, STT.StatusName, '' AS StackId, '' AS Position, SH2.ShelfId, SH2.ShelfName, SCM.AddedTime FROM tblCamera C JOIN tblStatus STT ON C.StatusId = STT.StatusId LEFT JOIN tblStackProductCameraMapping SPCM ON SPCM.CameraId = C.CameraId AND SPCM.StatusId = 1 LEFT JOIN tblShelfCameraMapping SCM ON C.CameraId = SCM.CameraId AND SCM.StatusId = 1 LEFT JOIN tblShelf SH2 ON SCM.ShelfId = SH2.ShelfId AND SH2.StatusId = 1 JOIN tblStore ST2 ON ST2.StoreId = SH2.StoreId AND ST2.StatusId != 2 AND ST2.StoreId IS NOT NULL WHERE C.CameraId = #{cameraId}
AND ST2.StoreId = #{storeId}
