mapper.select:
SELECT TR.totalOfRecord AS totalOfRecord, U.Fullname AS FullName, U.Username AS UserName, USM.StoreId AS StoreId, U.UpdatedTime AS UpdatedTime, S.StatusName AS StatusName FROM tblUser U JOIN tblStatus S ON U.statusID = S.StatusId JOIN tblRole R ON R.RoleId = U.roleId LEFT JOIN tblUserStoreMapping USM ON U.UserId = USM.UserID, (SELECT COUNT(U.UserId) AS totalOfRecord FROM tblUser U JOIN tblStatus S ON U.statusID = S.StatusId JOIN tblRole R ON R.RoleId = U.roleId LEFT JOIN tblUserStoreMapping USM ON U.UserId = USM.UserID WHERE UPPER(R.RoleName) = 'MANAGER'
AND U.Fullname like '%' #{searchValue} '%'
AND U.Username like '%' #{searchValue} '%'
AND UPPER(S.StatusName) = #{status}
) TR WHERE UPPER(R.RoleName) = 'MANAGER'
AND U.Fullname like '%' #{searchValue} '%'
AND U.Username like '%' #{searchValue} '%'
AND UPPER(S.StatusName) = #{status}
ORDER BY U.CreatedTime DESC LIMIT #{offSet}, #{fetchNext}

SELECT COUNT(UserName) AS TotaltOfRecord
FROM tblUser
WHERE UserName REGEXP #{userNameRegexp} OR UserName = #{userName};
