mapper.select:
SELECT TR.totalOfRecord, SH.ShelfId, SH.ShelfName, SH.UpdatedTime, SH.StatusId, SH.NumberStacks , STT.StatusName FROM tblShelf SH JOIN tblStore ST ON SH.StoreId = ST.StoreId
AND SH.StatusId = #{statusId}
AND ST.StoreId = #{storeId} JOIN tblUserStoreMapping USM ON ST.StoreId = USM.StoreId AND USM.StatusId = 1
JOIN tblStatus STT ON SH.StatusId = STT.StatusId, (SELECT COUNT(SH.ShelfId) AS totalOfRecord FROM tblShelf SH JOIN tblStore ST ON SH.StoreId = ST.StoreId
AND SH.StatusId = #{statusId}
AND ST.StoreId = #{storeId} JOIN tblUserStoreMapping USM ON ST.StoreId = USM.StoreId AND USM.StatusId = 1
JOIN tblStatus STT ON SH.StatusId = STT.StatusId
WHERE SH.ShelfName LIKE '%'#{shelfName}'%' ) AS TR
WHERE SH.ShelfName LIKE '%'#{shelfName}'%'
ORDER BY SH.ShelfName ASC LIMIT #{offSet}, #{fetchNext}

SELECT DISTINCT(SH1.ShelfId), SH1.StatusId, SH2.totalOfRecord
FROM tblShelf SH1,
(SELECT COUNT(ST.StackId) AS totalOfRecord
FROM  tblStack ST WHERE ST.ShelfId = #{shelfId}

    AND ST.StatusId != 2
) SH2
WHERE SH1.ShelfId = #{shelfId}

mapper.update:
UPDATE tblShelf SET StatusId = #{statusId}, UpdatedTime = convert_tz(now(),@@session.time_zone,#{updatedTime})

    , ReasonInactive = #{reasonInactive}

WHERE ShelfId = #{shelfId}
