mapper.select:
SELECT SUPQUE.totalOfRecord, S.StoreId, S.StoreName, S.ImageURL, S.Address, S.AnalyzedTime, S.ReasonInactive, TD.DistrictId, TD.DistrictName, TC.CityId, TC.CityName, TU.Username, TU.UserId, S.UpdatedTime, S.StatusId, TS.StatusName FROM tblStore S LEFT JOIN tblUserStoreMapping TUSM ON (S.StoreId = TUSM.StoreId AND TUSM.StatusId = 1) LEFT JOIN tblUser TU ON (TUSM.UserId = TU.UserId) LEFT JOIN tblStatus TS ON (S.StatusId = TS.StatusId) LEFT JOIN tblDistrict TD ON (S.DistrictId = TD.DistrictId) LEFT JOIN tblCity TC ON (TD.CityId = TC.CityId), (SELECT COUNT(*) AS totalOfRecord FROM tblStore S LEFT JOIN tblUserStoreMapping TUSM ON (S.StoreId = TUSM.StoreId AND TUSM.StatusId = 1) LEFT JOIN tblUser TU ON (TUSM.UserId = TU.UserId) LEFT JOIN tblStatus TS ON (S.StatusId = TS.StatusId) LEFT JOIN tblDistrict TD ON (S.DistrictId = TD.DistrictId) LEFT JOIN tblCity TC ON (TD.CityId = TC.CityId)
TC.CityName LIKE '%' #{searchValue} '%'
TD.DistrictName LIKE '%' #{searchValue} '%'
TU.Username LIKE '%' #{searchValue} '%'
S.StoreName LIKE '%' #{searchValue} '%'
AND S.StatusId = #{statusId}
AND TC.CityId = #{cityId}
) AS SUPQUE
TC.CityName LIKE '%' #{searchValue} '%'
TD.DistrictName LIKE '%' #{searchValue} '%'
TU.Username LIKE '%' #{searchValue} '%'
S.StoreName LIKE '%' #{searchValue} '%'
AND S.StatusId = #{statusId}
AND TC.CityId = #{cityId}
ORDER BY S.StoreName LIMIT #{offSet}, #{fetchNext}

SELECT S.StoreId, S.StoreName, S.ImageURL, S.Address, S.AnalyzedTime, S.ReasonInactive, TD.DistrictId, TD.DistrictName, TC.CityId, TC.CityName, TU.Username, TU.UserId, S.UpdatedTime, S.StatusId, TS.StatusName, TTT.totalOfRecord FROM (SELECT S.StoreId FROM tblStackProductCameraMapping B1 RIGHT JOIN tblStack B2 ON(B1.StackId = B2.StackId) RIGHT JOIN tblShelf B3 ON(B2.ShelfId = B3.ShelfId) RIGHT JOIN tblStore S ON(S.StoreId = B3.StoreId) WHERE B1.ProductId = #{productId} AND B1.RemoveProductTime IS NULL
AND S.StatusId = #{statusId}
GROUP BY S.StoreId) A1 LEFT JOIN tblStore S ON(S.StoreId = A1.StoreId) LEFT JOIN tblUserStoreMapping TUSM ON (S.StoreId = TUSM.StoreId AND TUSM.StatusId = 1) LEFT JOIN tblUser TU ON (TUSM.UserId = TU.UserId) LEFT JOIN tblStatus TS ON (S.StatusId = TS.StatusId) LEFT JOIN tblDistrict TD ON (S.DistrictId = TD.DistrictId) LEFT JOIN tblCity TC ON (TD.CityId = TC.CityId), (SELECT COUNT(*) AS totalOfRecord FROM (SELECT S.StoreId AS STO_ID FROM tblStackProductCameraMapping B1 RIGHT JOIN tblStack B2 ON(B1.StackId = B2.StackId) RIGHT JOIN tblShelf B3 ON(B2.ShelfId = B3.ShelfId) RIGHT JOIN tblStore S ON(S.StoreId = B3.StoreId) WHERE B1.ProductId = #{productId} AND B1.RemoveProductTime IS NULL
AND S.StatusId = #{statusId}
GROUP BY S.StoreId) TT ) TTT ORDER BY S.StoreName LIMIT #{offSet}, #{fetchNext}

SELECT COUNT(UserStoreMappingId) AS TotalOfRecord
FROM tblUserStoreMapping
WHERE UserId = #{userId}
    AND StoreId = #{storeId}
    AND StatusId = 1
